SELECT b.item_id, b.bill_rate, sum(b.bill_qty) sum_bill_qty, sum(b.bill_total) sum_bill_total, b.taxable_flag, i.description item_description FROM billing_v b, items i WHERE b.item_id = i.item_id AND b.status_id > 3 AND (taxable_flag = 'N' OR item_type_code = 'expense') AND b.invoice_id =  164325 GROUP BY b.item_id, b.invoice_description, i.description, b.bill_rate, b.taxable_flag