SELECT b.invoice_id, i.item_id, sum(b.bill_total) bill_rate, 1 sum_bill_qty, sum(b.bill_total) sum_bill_total, b.taxable_flag, i.description item_description FROM billing_v b, items i WHERE i.NAME='Labor-Tax' AND i.organization_id = b.organization_id AND b.status_id > 3 AND b.taxable_flag = 'Y' AND b.item_type_code = 'hours' AND b.invoice_id =  164325  GROUP BY b.invoice_id, i.item_id, i.description, b.taxable_flag